U8 MFC_MPIF_ReadByte(U8 u8slaveid, U16 u16Addr)
{
    U16 u16data;
    MDrv_MPIF_LC2A_RW(0, u8slaveid, (u16Addr & ~0x1), &u16data);
    return (u16Addr & 0x1) ? ((U8)(u16data>>8)) : ((U8)u16data);
}

BOOL MFC_MPIF_Write2Byte(U8 u8slaveid, U16 u16Addr, U16 u16Val)
{
    U16 u16temp;
    BOOL bRet = 1;
    if (u16Addr & 0x1)
    {
        // read & write high byte
        bRet &= MDrv_MPIF_LC2A_RW(0, u8slaveid, (u16Addr+1), &u16temp);
        u16temp = (u16temp & 0xFF00) | (u16Val >> 8);
        bRet &= MDrv_MPIF_LC2A_RW(1, u8slaveid, (u16Addr+1), &u16temp);

        // read & write low byte
        bRet &= MDrv_MPIF_LC2A_RW(0, u8slaveid, (u16Addr & ~0x1), &u16temp);
        u16temp = (u16temp & 0x00FF) | (u16Val << 8);
        bRet &= MDrv_MPIF_LC2A_RW(1, u8slaveid, (u16Addr & ~0x1), &u16temp);
    }
    else
    {
        bRet &= MDrv_MPIF_LC2A_RW(1, u8slaveid, u16Addr, &u16Val);
    }
    return bRet;
}


U16 MFC_MPIF_Read2Byte(U8 u8slaveid, U16 u16Addr)
{
    U16 u16data;
    U16 u16temp;
    if (u16Addr & 0x1)
    {
        // read high byte
        MDrv_MPIF_LC2A_RW(0, u8slaveid, (u16Addr+1), &u16temp);
        u16data = u16temp << 8;
        // read low byte
        MDrv_MPIF_LC2A_RW(0, u8slaveid, (u16Addr & ~0x1), &u16temp);
        u16data = u16data | (u16temp>>8);
    }
    else
    {
        MDrv_MPIF_LC2A_RW(0, u8slaveid, u16Addr, &u16data);
    }
    return u16data;
}

U32 MFC_MPIF_Read3Byte(U8 u8slaveid, U16 u16Addr)
{
    U32 u32data;
    U16 u16temp;
    if (u16Addr & 0x1)
    {
        // read high word
        MDrv_MPIF_LC2A_RW(0, u8slaveid, (u16Addr+1), &u16temp);
        u32data = ((U32)u16temp) << 8;
        // read low byte
        MDrv_MPIF_LC2A_RW(0, u8slaveid, (u16Addr & ~0x1), &u16temp);
        u32data = u32data | (u16temp>>8);
    }
    else
    {
        // read high byte
        MDrv_MPIF_LC2A_RW(0, u8slaveid, (u16Addr+2), &u16temp);
        u32data = ((U32)(u16temp & 0xFF))<<16;
        // read low word
        MDrv_MPIF_LC2A_RW(0, u8slaveid, u16Addr, &u16temp);
        u32data = u32data | u16temp;
    }
    return u32data;
}

BOOL MFC_MPIF_WriteByte(U8 u8slaveid, U16 u16Addr, U8 u8Val)
{
    U16 u16data;
    BOOL bRet = 1;
    bRet &= MDrv_MPIF_LC2A_RW(0, u8slaveid, (u16Addr & ~0x1), &u16data);
    if (u16Addr & 0x1)
        u16data = (u16data & 0x00FF) | (((U16)u8Val) << 8);
    else
        u16data = (u16data & 0xFF00) | (U16)u8Val;

    bRet &= MDrv_MPIF_LC2A_RW(1, u8slaveid, (u16Addr & ~0x1), &u16data);
    return bRet;
}

BOOL MFC_MPIF_Write3Byte(U8 u8slaveid, U16 u16Addr, U32 u32Val)
{
    U16 u16temp;
    BOOL bRet = 1;
    u32Val = u32Val & 0xFFFFFF;
    if (u16Addr & 0x1) // odd address
    {
        // write high word
        u16temp = (U16)(u32Val >> 8);
        bRet &= MDrv_MPIF_LC2A_RW(1, u8slaveid, (u16Addr+1), &u16temp);

        // read & write low byte
        bRet &= MDrv_MPIF_LC2A_RW(0, u8slaveid, (u16Addr & ~0x1), &u16temp);
        u16temp = (u16temp & 0x00FF) | (U16)(u32Val << 8);
        bRet &= MDrv_MPIF_LC2A_RW(1, u8slaveid, (u16Addr & ~0x1), &u16temp);
    }
    else // even address
    {
        // write high byte
        bRet &= MDrv_MPIF_LC2A_RW(0, u8slaveid, (u16Addr+2), &u16temp);
        u16temp = (u16temp & 0xFF00) | (U16)(u32Val >> 16);
        bRet &= MDrv_MPIF_LC2A_RW(1, u8slaveid, (u16Addr+2), &u16temp);

        // write low word
        u16temp = (U16)u32Val;
        bRet &= MDrv_MPIF_LC2A_RW(1, u8slaveid, u16Addr, &u16temp);
    }
    return bRet;
}

#define SPIF_REG_BASE                               (0x3700)
BOOL MFC_MPIF_Test(U8 u8slaveid, U8 u8TestLevel)
{
    #define MPIF_PRINT printk
    U8 j=0;
    U8 u8data;
    U16 u16data;

    //-- 1A Test ---------------------------
    if (u8TestLevel >= 1)
    {
        U8 kd;
        U8 _u8data[8] = {0x00, 0x01, 0x37, 0x73, 0x55, 0xAA, 0x5A, 0xA5};

        for (kd=2; kd<8; kd++)
        {
            u8data = _u8data[kd]+j;
            if(!MDrv_MPIF_LC1A_RW(1, u8slaveid, kd*2, &u8data))
            {
                MPIF_PRINT("LC1A index %u write NG\n", kd);
                return FALSE;
            }

            if(!MDrv_MPIF_LC1A_RW(0, u8slaveid, kd*2, &u8data))
            {
                MPIF_PRINT("LC1A index %u read NG\n", kd);
                return FALSE;
            }
            if(u8data != _u8data[kd]+j)
            {
                MPIF_PRINT("LC1A index %u NG, read data=0x%x, golden=0x%x\n", kd, u8data, _u8data[kd]+j);
                return FALSE;
            }
            MPIF_PRINT("LC1A index %u OK\n", kd);
        }
    }

    //-- 2A Test ---------------------------
    if (u8TestLevel >= 2)
    {
        u16data = 0x55AA+j;
        if(!MDrv_MPIF_LC2A_RW(1, u8slaveid, SPIF_REG_BASE+0x22, &u16data))
        {
            MPIF_PRINT("LC2A no checksum write NG\n");
            return FALSE;
        }
        if(!MDrv_MPIF_LC2A_RW(0, u8slaveid, SPIF_REG_BASE+0x22, &u16data))
        {
            MPIF_PRINT("LC2A no checksum read NG\n");
            return FALSE;
        }
        if(u16data != 0x55AA+j)
        {
            MPIF_PRINT("LC2A no checksum NG\n");
            return FALSE;
        }
        MPIF_PRINT("LC2A no checksum OK\n");
        u16data = 0x4747+j;
        if(!MDrv_MPIF_LC2A_RW(1, u8slaveid, SPIF_REG_BASE+0x22, &u16data))
        {
            MPIF_PRINT("LC2A checksum write NG\n");
            return FALSE;
        }
        if(!MDrv_MPIF_LC2A_RW(0, u8slaveid, SPIF_REG_BASE+0x22, &u16data))
        {
            MPIF_PRINT("LC2A checksum read NG\n");
            return FALSE;
        }
        if(u16data != 0x4747+j)
        {
            MPIF_PRINT("LC2A checksum NG\n");
            return FALSE;
        }
        MPIF_PRINT("LC2A checksum OK\n");
    }

    return TRUE;
}

