/// Serial debug mode
/// @param  MFC_SERIAL_DEBUG_CMD_e \b IN: command
/// @return TRUE/FALSE
//------------------------------------------------------------------------------
BOOL MDrv_MFC_SetSerialDebugMode(MFC_SERIAL_DEBUG_CMD_e cmd)
{
    U32  u32Addr;
    U8   u8Data[5];
	BOOL ret;

    u32Addr = 0;

    if (cmd == MFC_SerialDebugEnter)
    {
        u8Data[0] = 0x53;
        u8Data[1] = 0x45;
        u8Data[2] = 0x52;
        u8Data[3] = 0x44;
        u8Data[4] = 0x42;
        ret = MFC_IIC_Write(MFC_IIC_CHANNEL_ID, MFC_IIC_SLAVE_ADDR, (U8*)&u32Addr, 0,  u8Data, 5);
	//state = DDI_I2C_Write( I2C_DEV_FRC_MST7323S, 0, (UINT8*)&u32Addr, 5, u8Data, IIC_DEFAULT_RETRIES );//redcloud00@lge.com
        if (!ret)
            return FALSE;
    }
    else
    {
        u8Data[0] = cmd;
        ret = MFC_IIC_Write(MFC_IIC_CHANNEL_ID, MFC_IIC_SLAVE_ADDR, (U8*)&u32Addr, 0, u8Data, 1);
       // state = DDI_I2C_Write( I2C_DEV_FRC_MST7323S, 0, (UINT8*)&u32Addr, 1, u8Data, IIC_DEFAULT_RETRIES );//redcloud00@lge.com
        if (!ret)
            return FALSE;
    }
    return TRUE;
}

