void MDrv_MFC_TrueMotionDemo(U8 u8TrueMotionDemo)
{
	U8 u8buf;

    u8buf = MDrv_MFC_ReadByte(0x290E)&0x0f;
    if(u8buf == 0)
    	MDrv_MFC_WriteByte(0x290E, 0x2F); // Memc on
    MDrv_MFC_WriteByteMask(0x2C44, 0x02, 0x07);	//MEMC high

	switch (u8TrueMotionDemo)
	{
		case 0: // Demo off, Memc on
			u8buf = 0x00;
			break;

		case 1: // Demo left side
			u8buf = 0x10;
			break;

		case 2: // Demo right side
			u8buf = 0x90;
			break;

		case 3: // Demo top side
			u8buf = 0x20;
			break;

		case 4: // Demo bottom side
			u8buf = 0xA0;
			break;

		default: // Demo off, Memc off
			u8buf = 0x00;
			break;
	}

	if (u8buf)//Film 22 32 mode detect
		MDrv_MFC_WriteByteMask(0x2080, 0x00, 0x03);
	else
		MDrv_MFC_WriteByteMask(0x2080, 0x03, 0x03);

	MDrv_MFC_WriteByteMask(0x2981, u8buf, 0xF0);

    //printf("\n\r\n\r =========   MDrv_MFC_TrueMotionDemo %d  ============\n\r\n\r", u8TrueMotionDemo);
}

void MDrv_MFC_VideoBlock(U8 u8Type , BOOL bOnOff)
{

    switch (u8Type)
    {
    	case 0: // URSA Rx (receiver signal off)
    		if(bOnOff)
    		{//Enable Rx
    			MDrv_MFC_WriteByteMask(0x2F4C, 0x01, 0x03);
    			MDrv_MFC_WriteByte(0x2F4E, 0x0F);
    			MDrv_MFC_WriteByte(0x2EE0, 0x01);
    			MDrv_MFC_WriteByte(0x3002, 0x00);
    			MDrv_MFC_WriteByte(0x3016, 0x00);
    		}
    		else
    		{//disable Rx
    			MDrv_MFC_WriteByteMask(0x2F4C, 0x00, 0x03);
    			MDrv_MFC_WriteByte(0x2F4E, 0x00);
    			MDrv_MFC_WriteByte(0x2EE0, 0x00);
    			MDrv_MFC_WriteByte(0x3002, 0x08);
    			MDrv_MFC_WriteByte(0x3016, 0x02);
    		}
    		break;
    	case 1: // URSA Tx (output black mute)
    		if(bOnOff)
    		{
	    		//R,G,B 10bit data setting
	    		MDrv_MFC_WriteByte(0x302a, 0x00);
	    		MDrv_MFC_WriteByte(0x302b, 0x00);
	    		MDrv_MFC_WriteByte(0x302c, 0x00);
	    		MDrv_MFC_WriteByte(0x302d, 0x00);
	    		MDrv_MFC_WriteByte(0x302e, 0x00);
	    		MDrv_MFC_WriteByte(0x302f, 0x00);
	    		//disable Rxd
	    		MDrv_MFC_WriteByte(0x3017, 0x04);
	    		MDrv_MFC_WriteByte(0x3002, 0x00);
	    		MDrv_MFC_WriteByte(0x3016, 0x00);
	    	}
	    	else
	    	{//enable Rx
		    	MDrv_MFC_WriteByte(0x3017, 0x00);
		    	MDrv_MFC_WriteByte(0x3002, 0x08);
		    	MDrv_MFC_WriteByte(0x3016, 0x02);
	    	}
	    	break;
	    case 2: // LVDS (LVDS signal off)
	    	if(bOnOff)
	    	{//disable LVDS
		    	MDrv_MFC_WriteByte(0x3220, 0x00);
		    	MDrv_MFC_WriteByte(0x3221, 0x00);
		    	MDrv_MFC_WriteByte(0x3222, 0x00);
		    	MDrv_MFC_WriteByte(0x3223, 0x00);
		    	MDrv_MFC_WriteByte(0x3224, 0x00);
		    	MDrv_MFC_WriteByte(0x3225, 0x00);
		    	MDrv_MFC_WriteByte(0x3226, 0x00);
	    	}
	    	else
	    	{//enable LVDS
		    	MDrv_MFC_WriteByte(0x3220, 0x55);
		    	MDrv_MFC_WriteByte(0x3221, 0x55);
		    	MDrv_MFC_WriteByte(0x3222, 0x55);
		    	MDrv_MFC_WriteByte(0x3223, 0x55);
		    	MDrv_MFC_WriteByte(0x3224, 0x55);
		    	MDrv_MFC_WriteByte(0x3225, 0x55);
		    	MDrv_MFC_WriteByte(0x3226, 0x05);
	    	}
	    	break;
	    default:
	    	break;
	}

}

