//==========================================================
//Description:
//ucGroupIndex     : 0~5
//				 : PWM0 ~PWM5
//ucFrequency	 : PWM frequency
//bVsyncEn		 : PWM synchronize with Vsync
//Period			 : 18bit
//ucDuty			 : Duty= period/ucDuty  /2=0.5 , /4=0.25 , /8=0.125
//==========================================================
void MDrv_MFC_SetPWMFreq(U8 u8GroupIndex , U8 u8Frequency, BOOL bVsyncEn, U32  u32Duty, U32 u32Shift, BOOL bShiftEn)
{
	U8 u8Divider;
	U16 u16Ext;
	U32 u32Period;

	MDrv_MFC_Write2Bytes(0x1E3E , 0x6000); //PWM_SEL(only  pwm0/pwm1, others need check)
	MDrv_MFC_Write2Bytes(0x1E40 , MDrv_MFC_Read2Bytes(0x1E40) & (~(0x01 << u8GroupIndex))); //PWM_OEN	//set if >2 pwm use

	//Div
	u8Divider =1;
	MDrv_MFC_WriteByte(0x2508 + (u8GroupIndex*6), u8Divider-1); // [7:0] Divider

	//Period
	u32Period = msCalculateDecimal( (U32)PWM_CRYSTAL_CLOCK, (U32)u8Frequency*u8Divider);
	//printf("\n\n\n\n\n\n\n\n\n\n\n\n\n\n\n\n\n u32Period  = %x\n\n\n\n\n\n\n\n", u32Period);
	MDrv_MFC_Write2Bytes(0x2504 + (u8GroupIndex*6), (u32Period & PERIOD_MASK)-1); 	//Period //Actual Value= Reg value +1
	u16Ext = (u32Period>>16) & 0x03;
	u16Ext = (u16Ext << (u8GroupIndex*2)) |MDrv_MFC_Read2Bytes(0x2540);
	MDrv_MFC_Write2Bytes(0x2540 , u16Ext); //set if >2 pwm use

	//Duty
	//printf("\n\n\n\n\n\n dwDuty_1  = %x\n\n\n\n\n\n", u32Duty);
	u32Duty = u32Period/u32Duty ;
	MDrv_MFC_Write2Bytes(0x2506 + (u8GroupIndex*6), u32Duty-1); //Duty
	//printf("\n\n\n\n\n\n dwDuty_2  = %x\n\n\n\n\n\n", u32Duty);

	//Shift
	if(bShiftEn)
	{
		//u32Shift = u32Period * ( u32Shift/360) ;
		u32Shift = msCalculateDecimal( (U32)u32Period*u32Shift, 360);
		MDrv_MFC_Write3Bytes(0x2550 + (u8GroupIndex*4), u32Shift-1); //Shift
		//printf("\n\n\n\n\n\n u32Shift  = %x\n\n\n\n\n\n", u32Shift);

		u32Duty =  u32Shift + u32Duty ;
		//printf("\n\n\n\n\n\n dwDuty  = %x\n\n\n\n\n\nn", u32Duty);
		MDrv_MFC_Write2Bytes(0x2506 + (u8GroupIndex*6), u32Duty-1); //Duty
		u16Ext = (u32Duty>>16) & 0x03;
		u16Ext = (u16Ext << (u8GroupIndex*2)) |MDrv_MFC_Read2Bytes(0x2542);
		MDrv_MFC_Write2Bytes(0x2542 , u16Ext); //set if >2 pwm use
	}

	if(bVsyncEn)
		MDrv_MFC_WriteByteMask(0x2509+(u8GroupIndex*6) , _BIT2|_BIT1, _BIT2|_BIT1);  //[2,1]: Vsync reset En,double buffer En

	//printf("\r\n ucGroupIndex  = %d", ucGroupIndex);
	//printf("\r\n ucFrequency  = %d", ucFrequency);
	//printf("\r\n u32Period  = %x", u32Period);
	//printf("\r\n u8Divider  = %x", u8Divider);

	//Additional Vsync
	//MDrv_MFC_WriteBit(0x3235, 1, _BIT5);
	//MDrv_MFC_WriteByteMask(0x3223, 0, _BIT3|_BIT2);
}












