U8 firstMC_end;
U8 beforeFirstME;
void getFirstMC(void)
{
    if (gmfcSysInfo.u8PanelVfreq==60)
    {
        if ( gMfcType.Film22Mode )
            firstMC_end = !gMfcType.Film22Idx;
        else if ( gMfcType.Film32Mode )
            firstMC_end = ( 0 == gMfcType.Film32Idx ) || ( 2 == gMfcType.Film32Idx );
        else
          firstMC_end = 1;
    }
    else
    {
        if ( gMfcType.Film22Mode )
            firstMC_end = !gMfcType.Film22Idx && !gMfcType.FDUP;
        else if ( gMfcType.Film32Mode )
            firstMC_end = ( 0 == gMfcType.Film32Idx && !gMfcType.FDUP ) || ( 2 == gMfcType.Film32Idx && gMfcType.FDUP );
        else
            firstMC_end = !gMfcType.FDUP;

        if ( gMfcType.Film22Mode )
            beforeFirstME = gMfcType.Film22Idx && gMfcType.FDUP;
        else if ( gMfcType.Film32Mode )
            beforeFirstME = ( 4 == gMfcType.Film32Idx && gMfcType.FDUP ) || ( 2 == gMfcType.Film32Idx && !gMfcType.FDUP );
        else
            beforeFirstME = gMfcType.FDUP;
    }
}


