//------------------------------------------------------------------------------
/// MFC Load Bin to SRAM
//------------------------------------------------------------------------------
BOOL MDrv_MFC_LoadBinToSram(U8* pBin, U32 len)
{
    U8   i;
    U8   u8Addr[3];
    BOOL ret;

	len=len;
    MDrv_MFC_WriteByte(0x10E6, 0x01); // enable xdata mapping to psam
    for (i=0; i<16; i++)
    {
        // Switch page, every page is 1k bytes
        ret = MDrv_MFC_WriteByte(0x10E4, i); // Switch page, every page is 1k bytes
        if (!ret)
            return FALSE;

        // downloading
        u8Addr[0] = 0x10;
        u8Addr[1] = 0x50;
        u8Addr[2] = 0x00;
        ret = MAdp_IIC_Write(MFC_IIC_CHANNEL_ID, 0xB4, u8Addr, 3, pBin, 1024);
        if (!ret)
            return FALSE;

        pBin += 1024;
    }
    MDrv_MFC_WriteByte(0x10E4, 0x00); // Switch page, every page is 1k bytes
    MDrv_MFC_WriteByte(0x10E6, 0x00); // disable xdata mapping to psam

    return TRUE;
}

BOOL MDrv_MFC_LoadGammaDAC(U8* pTbl, U32 len)
{
    U8   u8Addr, GAMMA_SLAVE_ADDRESS = 0xE8;
    BOOL ret;

    // downloading
    u8Addr = 0x00;
    ret = MAdp_IIC_Write(0x05, GAMMA_SLAVE_ADDRESS, &u8Addr, 1, pTbl, len);
    if (!ret)
        return FALSE;
    return TRUE;
}

BOOL MDrv_MFC_LoadGammaVCOM(U8* pTbl, U32 len)
{
    U8   u8Addr, GAMMA_SLAVE_ADDRESS = 0xE8;
    BOOL ret;

    // downloading
    u8Addr = 0x12;
    ret = MAdp_IIC_Write(0x05, GAMMA_SLAVE_ADDRESS, &u8Addr, 1, pTbl, len);
    if (!ret)
        return FALSE;
    return TRUE;
}

void MDrv_MFC_GetChipRevision(void)
{
    gmfcSysInfo.u8ChipRevision =  MDrv_MFC_ReadByte(0x1E3D);
	if(gmfcSysInfo.u8ChipRevision== 0x00)
        gmfcSysInfo.u8ChipRevision = MST_3xxx_U01;         //U01
	else if(gmfcSysInfo.u8ChipRevision== 0x01)
        gmfcSysInfo.u8ChipRevision =  MST_3xxx_U02;        //U02
	else if(gmfcSysInfo.u8ChipRevision== 0x02)
        gmfcSysInfo.u8ChipRevision =  MST_3xxx_U03;        //U03
	else
        gmfcSysInfo.u8ChipRevision =  MST_3xxx_U03;        //U03
}

BOOL MDrv_MFC_WaitReady(U8 u8WaitValue)
{
	U8 u8ReadValue, u8count = 0;
	while(u8count++ < 10)
	{
                MDrv_MFC_SetSerialDebug(FALSE);
        mfcSleepMs(50);
		u8ReadValue=0x00;
                MDrv_MFC_SetSerialDebug(TRUE);
		u8ReadValue = MDrv_MFC_ReadByte(0x2C45)&0x0F;
		
		if(u8ReadValue == u8WaitValue)
			break;
	}
	return (u8count < 10) ? 1 : 0;
}

void MDrv_MFC_SramLoadCode(void)
{
#if !MFC_USE_IN_BOOLLOAD

	U8 *pAddr;
	U32 Len;

    //20090724 Terry, URSA ISP Load Code
    // U32 k;
    MAdp_getSPI(MFC_BIN_SPI);
    // for(k=0;k<100;k++) printf("SPI:  %X\n",MFC_BIN_SPI[k]);
    // header [0~3]: check valid  [4~7]: bin size
    if((MFC_BIN_SPI[0]==0x11)&&(MFC_BIN_SPI[1]==0x22)&&(MFC_BIN_SPI[2]==0x33)&&(MFC_BIN_SPI[3]==0x44))
    {
        pAddr = &MFC_BIN_SPI[8];
        Len=(((U32)(MFC_BIN_SPI[4]<<24))+((U32)(MFC_BIN_SPI[5]<<16))+((U32)(MFC_BIN_SPI[6]<<8))+((U32)(MFC_BIN_SPI[7])));
    }
    else

    {
    	pAddr = MFC_BIN;
        Len = sizeof(MFC_BIN);
    }

    //dbgprint("Ursa binary size = 0x%x (Max is 0x4000)\n", Len);
    if(Len>0x4000)
    {
        //dbgprint("^r^ !!!!!!!!!!!! FATAL ERROR : over 16K !!!!!!!!!!!!.....\n");
        while(1);
    }


    MDrv_MFC_LoadBinToSram(pAddr, Len);
    MDrv_MFC_WriteByte(0x1084, 0x01); // Sram boot code reset release
    MDrv_MFC_WriteByteMask(0x2C45, 0, 0x0F);
#endif

}


