void MDrv_MFC_BringUp(void)
{
    MDrv_MFC_SetInterrupt(_DISABLE);
    MDrv_MFC_McuInitialize();
    MDrv_MFC_WriteBit(0x2F00, 0, _BIT3);	//Dubber buffer dis
    MDrv_MFC_WriteBit(0x2F00, 0, _BIT0);	//SW reset

    MDrv_MFC_WriteBit(0x1E03, 1, _BIT0);	//SCaler reset Hold
    MDrv_MFC_WriteBit(0x1E49, 0, _BIT0);	//clear this status
    MDrv_MFC_InitializeChip();

    // SPIF settings
    MDrv_MFC_WriteByteMask(0x1E38, _BIT3, _BIT3);    // swap M/SPIF
    MDrv_MFC_WriteByte(0x1E8D, 0x18);   // set SPIF clock, 0x18: 12MHz
    //MDrv_MFC_WriteByte(0x1E8D, 0x10);   // set SPIF clock, 0x10: 108MHz
    MAdp_MPIF_Open();
    MAdp_MPIF_Init(0x3C, 0x1, 0x1); //0x3C: 12MHz
    //MAdp_MPIF_Init(0x04, 0x1, 0x1); //0x04:123MHz

    gmfcSysInfo.u8UseMPIF = MAdp_MPIF_Test(MFC_SPIF_SLAVE_ID, 1);
    if (gmfcSysInfo.u8UseMPIF)
    {
        MFC_REG_DEBUG (printf("********* Has MPIF/SPIF************\n"));
        MAdp_MPIF_InitSPIF(MFC_SPIF_SLAVE_ID);
        MAdp_MPIF_SetCmdDataWidth(MFC_SPIF_SLAVE_ID, MPIF_DATAWIDTH_1, MPIF_DATAWIDTH_1);
        gmfcSysInfo.u8UseMPIF = 2;
    }
    MDrv_MFC_InitializeScalerOP();
    MDrv_MFC_InitializeMiu();
	MDrv_MFC_InitializeScalerIP();
	MDrv_MFC_InitializeFilmMode();
	MDrv_MFC_InitializePanel();
    #if (GAMMA_FUNCTION != GAMMA_OFF)
	if(gmfcSysInfo.u8PanelType == _MINI_LVDS || gmfcSysInfo.u8PanelType == _MINI_LVDS_GIP || gmfcSysInfo.u8PanelType == _MINI_LVDS_GIP_V5)
	{
		MDrv_MFC_LoadGammaDAC(tInitializeDAC, gDACLen);
		MDrv_MFC_LoadGammaVCOM(tInitializeVCOM, gVCOMLen);
	}
    #endif
	MDrv_MFC_PowerDownChipU3();

    if( (MDrv_MFC_ReadByte(0x1E03)& 0x01) != 0)
        MDrv_MFC_WriteBit(0x1E03, 0, _BIT0);
    MDrv_MFC_WriteBit(0x2F00, 1, _BIT3);	//Dubber buffer En
    MDrv_MFC_WriteBit(0x2F00, 1, _BIT0);    //SW reset release.


    if(gmfcSysInfo.u8PanelType == _MINI_LVDS_GIP || gmfcSysInfo.u8PanelType == _MINI_LVDS_GIP_V5)
    {
    //printf("\n\r ==== start to power on sequence.====");
    MDrv_MFC_WriteByte(0x23F5, 0x00); //POL/SOE

    mfcSleepMs(10); // DPM take long time to pull high. change 20ms to 10ms.
    MDrv_MFC_WriteBit(0x1E22, 0, _BIT1);  // DPM gpio1 enable.
    MDrv_MFC_WriteBit(0x1E1A, 1, _BIT1);  // DPM enable.
    MDrv_MFC_WriteBit(0x1E22, 0, _BIT3);  // gpio3 enable.
    MDrv_MFC_WriteBit(0x1E1A, 0, _BIT3);  // gpio3 set to Low.
        mfcSleepMsNop(10); //j091217
        MDrv_MFC_WriteBit(0x23F9, 0, _BIT6); //Gating GPO CLK release//j091216
    }
    if(gmfcSysInfo.u8PanelType==_MINI_LVDS)
    {
        mfcSleepMsNop(10); //j091217
        MDrv_MFC_WriteBit(0x23F9, 0, _BIT6); //Gating GPO CLK release//j091216

        MDrv_MFC_WriteBit(0x238C, 0, _BIT0);	//Output Enable FLK OFF,GOE OFF
		mfcSleepMs(10);
		MDrv_MFC_WriteBit(0x23F5, 0, _BIT4);	//GSC OFF
		mfcSleepMs(10);
		MDrv_MFC_WriteBit(0x23F5, 0, _BIT2|_BIT3);	//POL OFF,SOE OFF
		mfcSleepMs(10);
		MDrv_MFC_WriteBit(0x23F5, 0, _BIT7);	//GSP OFF
		mfcSleepMs(10);
		MDrv_MFC_WriteBit(0x1E1A, _BIT1, _BIT1);	//DPM OFF
        mfcSleepMs(60);
        MDrv_MFC_WriteBit(0x23F5, 0, _BIT5);	//GOE ON
		//printf("\n\n\n\n\n\n\n\n\n\n move mini lvds power sequence to S7M bring up\n\n\n\n\n\n\n\n\n\n");
    }
    //MDrv_MFC_SetInterrupt(_ENABLE);

    //LVDS Data enable
    if(gmfcSysInfo.u8PanelType == _LVDS)
    {
        MDrv_MFC_Write2Bytes(0x3220, 0x5555); // set output configure to lvds output = "01" for 26 channel
	    MDrv_MFC_Write2Bytes(0x3222, 0x5555);
	    MDrv_MFC_Write2Bytes(0x3224, 0x5555);
	    MDrv_MFC_WriteByte(0x3226, 0x05);
    }
}

