void MDrv_MFC_OnOff(BOOL bOnOff)
{
	if (bOnOff)
	{
        u8MFCLevelFlag = 1;
        MDrv_MFC_WriteByteMask(0x2C43, u8PreBlur, 0x0F);
        MDrv_MFC_WriteByteMask(0x2C43, u8PreJudder<<4, 0xF0);
		MDrv_MFC_WriteByteMask(0x2C44, u8PreMFCLevel, MFC_LEVEL_MASK); // Memc on
	}
	else
	{
	    u8MFCLevelFlag = 0;
        u8PreBlur = MDrv_MFC_ReadByte(0x2C43)&0x0F;
        u8PreJudder= MDrv_MFC_ReadByte(0x2C43)>>4;
	    u8PreMFCLevel = MDrv_MFC_ReadByte(0x2C44)& MFC_LEVEL_MASK;
		MDrv_MFC_WriteByteMask(0x2C44, 0, 0x07); // Memc off
	}
}

void MDrv_MFC_COMPENSATION(U8 u8Blur, U8 u8Judder, MFC_LEVEL u8MFC)
{
    //need to be implement for
    //Blur
    //Judder
    //MFC hi/mid/lo/off/5:5 pull down
    if(u8Blur>10) u8Blur=10;
    if(u8Judder>10) u8Judder=10;
    u8PreBlur = u8Blur;
    u8PreJudder= u8Judder;
    u8PreMFCLevel = u8MFC;

    if(u8MFCLevelFlag == 1)
    {
        MDrv_MFC_WriteByteMask(0x2C43, u8PreBlur, 0x0F);
        MDrv_MFC_WriteByteMask(0x2C43, u8PreJudder<<4, 0xF0);
        MDrv_MFC_WriteByteMask(0x2C44, u8PreMFCLevel, MFC_LEVEL_MASK);
    }
}

