void MDrv_MFC_Set_Bypass_Window(BOOL bOnOff, U8 u8WindowID, U16 u16HStart, U16 u16VStart, U16 u16HSize, U16 u16VSize)
{
	static U8 u8winStatus = 0;
	U16 u16Hend, u16Vend, temp;

	u16Hend = u16HStart+u16HSize;
	u16Vend = u16VStart+u16VSize;

    switch(u8MFCMirrorStatus)
    {
        case MIRROR_H_MODE:
            temp = gmfcSysInfo.u16Width - u16Hend;
            u16Hend = gmfcSysInfo.u16Width - u16HStart;
            u16HStart = temp;
            break;

        case MIRROR_V_MODE:
            temp = gmfcSysInfo.u16Height - u16Vend;
            u16Vend = gmfcSysInfo.u16Height - u16VStart;
            u16VStart = temp;
            break;

        case MIRROR_HV_MODE:
            temp = gmfcSysInfo.u16Width - u16Hend;
            u16Hend = gmfcSysInfo.u16Width - u16HStart;
            u16HStart = temp;

            temp = gmfcSysInfo.u16Height - u16Vend;
            u16Vend = gmfcSysInfo.u16Height - u16VStart;
            u16VStart = temp;
            break;

        case MIRROR_OFF:
        default:
            break;
    }
	if(u16HStart < 0x60)
		u16HStart = 0;

    MDrv_MFC_WriteByteMask(0x2440, 0x11, 0x11);//OSD control window enable
	MDrv_MFC_WriteByte(0x2444+u8WindowID*8, u16HStart); // H start
	MDrv_MFC_WriteByte(0x2445+u8WindowID*8, (u16HStart&0X0F00)>>8);
	MDrv_MFC_WriteByte(0x2446+u8WindowID*8, u16Hend); // H end
	MDrv_MFC_WriteByte(0x2447+u8WindowID*8, (u16Hend&0X0F00)>>8);
	MDrv_MFC_WriteByte(0x2448+u8WindowID*8, u16VStart); // V start
	MDrv_MFC_WriteByte(0x2449+u8WindowID*8, (u16VStart&0X0F00)>>8);
	MDrv_MFC_WriteByte(0x244A+u8WindowID*8, u16Vend); // V end
	MDrv_MFC_WriteByte(0x244B+u8WindowID*8, (u16Vend&0X0F00)>>8);

	if(bOnOff)
	{
		if(u8WindowID <= 4)
			u8winStatus |= 0x01<<u8WindowID;	//enable selected window
		else
			u8winStatus = 0x1f;	//enable all windows
	}
	else
	{
		if(u8WindowID <= 4)
			u8winStatus &= ~(0x01<<u8WindowID);	//disable selected window
		else
			u8winStatus = 0; //disable all windows
	}

	MDrv_MFC_WriteByte(0x246C, u8winStatus);
}

