void MDrv_MFC_DemoBarControl(BOOL bEnable, BOOL bDirection, U8 u8width, U8 u8color)
{
	U8 u8buf, u8Buf2;
	U16 u16frameActiveXStart, u16imageActiveXStart;

    u8buf = MDrv_MFC_ReadByte(0x2F19);
    u16frameActiveXStart=u8buf<<8;
    u8buf = MDrv_MFC_ReadByte(0x2F18);
    u16frameActiveXStart |=u8buf;
    u8buf = MDrv_MFC_ReadByte(0x2F21);
    u16imageActiveXStart=u8buf<<8;
    u8buf = MDrv_MFC_ReadByte(0x2F20);
    u16imageActiveXStart |=u8buf;

    u8buf = MDrv_MFC_ReadByte(0x2F57);
	if(bEnable)
	{
		u8buf = (u8buf & 0x7F) | 0x80; 	//Line enable 0x2F57[7]
		if(bDirection)
			u8buf = (u8buf & 0xBF) | 0x40;	//Vertical line direction 0x2F57[6]
		else
			u8buf =  (u8buf & 0xBF) | 0x00;	//Horizontal line direction 0x2F57[6]
		u8buf = (u8buf & 0xF0) | (u8width & 0x0F);	//Line width 0x2F57[0:3]
		// vertical bar shift is related by HStart.
        if (u16frameActiveXStart>u16imageActiveXStart)
        {
            u8Buf2=(u16imageActiveXStart>(u16frameActiveXStart>>1))? (u16imageActiveXStart-(u16frameActiveXStart>>1)) : ((u16frameActiveXStart>>1)-u16imageActiveXStart);
        }
        else
        {
            u8Buf2= u16imageActiveXStart-(u16frameActiveXStart>>1);
            u8buf = u8buf | 0x20;	// direction for shift Line 0x2F57[5], 0:R, 1:L
        }
		MDrv_MFC_WriteByte(0x2F56, u8Buf2);	// shift Line
		MDrv_MFC_WriteByte(0x2F57, u8buf);	// creat Line
		MDrv_MFC_WriteByte(0x30A1, u8color);//color 0~255
	}
	else
	{
		MDrv_MFC_WriteByte(0x2F57, (u8buf&0x7F)|0x00);	//Line disable
	}
}

