void MDrv_MFC_FrameLockMode(U8 u8Mode)
{
	switch (u8Mode)
	{
		case 0: 	//disable controling	(no setting for Dclk, HV total)
			MDrv_MFC_WriteByteMask(0x1E4C, 0x80, 0xC0);		// bit [7:6]
			break;
		case 1: 	//frame lock
			MDrv_MFC_WriteByteMask(0x1E4C, 0x00, 0xC0);		// bit [7:6]
			break;
		case 2: 	//freerun
			MDrv_MFC_WriteByteMask(0x1E4C, 0x40, 0xC0);		// bit [7:6]
			break;
		default:
			MDrv_MFC_WriteByteMask(0x1E4C, 0x00, 0xC0);		// bit [7:6]
			break;
	}
}

void MDrv_MFC_Reset(void)
{
    MDrv_MFC_WriteBit(0x1E03, 1, _BIT0);
    MDrv_MFC_WriteBit(0x1E03, 0, _BIT0);
}

void MDrv_MFC_UpdateSW(void)
{
	MDrv_MFC_SramLoadCode();
    MDrv_MFC_WaitReady(1);
}

void MDrv_MFC_SetReverseMode(U8 u8MirrorMode, U8 isS7M)
{
    u8MFCMirrorStatus = u8MirrorMode;
    if(isS7M)
    {
        //MDrv_MFC_SetMirrorMode(u8MirrorMode);
        MDrv_MFC_WriteByteMask(0x2C42, (u8MirrorMode<<6), 0xC0);
    }
    else
    {
        MDrv_MFC_WriteByteMask(0x2C42, (u8MirrorMode<<6), 0xC0);
    }
}
void MDrv_MFC_SetByPassWindowReverse(U8 u8Mode)
{
    u8MFCMirrorStatus = u8Mode;
}

void MDrv_MFC_SetLVDSBit(U8 u8BitNum)
{
	if(u8BitNum==6)
		u8BitNum = 0x0C;
	else if(u8BitNum==8)
		u8BitNum = 0x08;
	else
		u8BitNum = 0x00;

	//0x320A=0x08:8bits TI mode, 0x0C:6bits TI mode, 0x00:others
	MDrv_MFC_WriteByte(0x320A, u8BitNum);

}


