void MDrv_MFC_SetOPMFC(BOOL bEnable, BOOL bIs8Bits)
{
    MDrv_MFC_WriteBit(0x307E, bEnable, _BIT0);
    MDrv_MFC_WriteBit(0x307E, bIs8Bits, _BIT2);
}




